import React, { Component } from "react";


import "./login.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import './module.css';
import {Container , Row, Col} from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import * as Icon from 'react-bootstrap-icons';

import background from "./loginback.gif";
export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleSubmit(e) {
    e.preventDefault();
    const { email, password } = this.state;
    console.log(email, password);
    fetch("http://localhost:5000/login-user", {
      method: "POST",
      crossDomain: true,
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
        email,
        password,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data, "userRegister");
        if (data.status == "ok") {
          alert("login successful");
          window.localStorage.setItem("token", data.data);
          window.location.href = "./userDetails";
        }
        else
        {
          alert("login failed");
        }
      });
  }
  render() {
    return (
      <div style={{ 
        backgroundImage: `url(${background})`,backgroundRepeat:"no-repeat", backgroundSize: 'cover' }} className="outline">
          <div className="inner">
      <form onSubmit={this.handleSubmit}>
        <h3 style={{paddingLeft:"145px"}}>Sign In</h3>

        <div className="allign">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            placeholder="Enter email"
            onChange={(e) => this.setState({ email: e.target.value })}
          />
        </div>

        <div className="allign">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Enter password"
            onChange={(e) => this.setState({ password: e.target.value })}
          />
        </div>

        <div className="allign">
          <div className="custom-control custom-checkbox">
            <input
              type="checkbox"
              className="custom-control-input"
              id="customCheck1"
            />
            <label className="custom-control-label" htmlFor="customCheck1">
             
              Remember me
            </label>
          </div>
        </div>

        <div className="subc">
          <Container fluid>
            <Row>
              <Col>
          <Button type="submit"variant="outline-dark">
            Login <Icon.ArrowBarRight />
          </Button></Col>
        <Col></Col>
       
          <a href="/sign-up">  <Button type="submit"variant="outline-dark">
            Sign up<Icon.ArrowBarRight />
          </Button></a>
          </Row>
          </Container>
    </div>
      </form>
      </div>
      </div>
    );
  }
}
